select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND b.Date>='2010-07-27 00:28:43'::timestamp  AND ph.PostHistoryTypeId=2  AND pl.LinkTypeId=1  AND p.CommentCount=0  AND p.FavoriteCount>=0  AND p.FavoriteCount<=9;
