select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND c.CreationDate<='2014-09-10 02:09:02'::timestamp  AND p.CreationDate>='2010-07-19 19:28:12'::timestamp  AND u.Reputation<=186  AND u.DownVotes>=0  AND u.UpVotes>=0;
