select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND ph.PostHistoryTypeId=5  AND u.Views<=76  AND u.DownVotes>=0  AND u.DownVotes<=0  AND v.CreationDate>='2010-07-21 00:00:00'::timestamp;
