select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND ph.PostHistoryTypeId=2  AND p.PostTypeId=1  AND p.ViewCount<=4063  AND p.AnswerCount>=0  AND p.AnswerCount<=3  AND p.CreationDate>='2010-07-30 17:10:24'::timestamp  AND u.UpVotes=0  AND v.CreationDate>='2010-07-21 00:00:00'::timestamp  AND v.CreationDate<='2014-09-10 00:00:00'::timestamp;
