select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND c.CreationDate<='2014-09-12 19:10:05'::timestamp  AND pl.CreationDate<='2014-07-15 19:49:54'::timestamp  AND p.PostTypeId=1  AND p.Score<=12  AND u.UpVotes=0  AND v.VoteTypeId=2;
