select  count(*) from comments as c,  		posts as p,  		postHistory as ph,          votes as v,          users as u  where u.Id = c.UserId 	and c.UserId = p.OwnerUserId 	and p.OwnerUserId = ph.UserId 	and ph.UserId = v.UserId  AND c.Score=2  AND ph.PostHistoryTypeId=2  AND p.ViewCount>=0  AND p.ViewCount<=7574  AND p.AnswerCount=0  AND p.FavoriteCount=0  AND v.CreationDate<='2014-09-13 00:00:00'::timestamp;
