select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND c.Score=0  AND c.CreationDate>='2010-08-16 11:50:18'::timestamp  AND c.CreationDate<='2014-09-04 22:52:53'::timestamp  AND p.Score>=-5  AND p.CommentCount<=10  AND u.Views>=0  AND u.Views<=28  AND u.DownVotes>=0  AND v.BountyAmount<=100;
