select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND ph.CreationDate<='2014-05-07 07:13:47'::timestamp  AND pl.LinkTypeId=1  AND pl.CreationDate>='2010-11-26 02:38:57'::timestamp  AND pl.CreationDate<='2014-08-19 13:17:11'::timestamp  AND p.Score<=23  AND p.FavoriteCount>=0  AND v.CreationDate<='2014-09-09 00:00:00'::timestamp;
