select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND pl.LinkTypeId=1  AND pl.CreationDate>='2010-09-12 15:48:10'::timestamp  AND pl.CreationDate<='2014-07-27 17:36:32'::timestamp  AND p.AnswerCount>=0  AND p.AnswerCount<=5  AND p.CreationDate>='2010-09-21 18:13:57'::timestamp  AND u.Reputation>=1  AND u.Reputation<=651  AND u.DownVotes>=0  AND u.UpVotes>=0  AND v.VoteTypeId=2;
