select  count(*) from postHistory as ph,          posts as p,  		votes as v,  		users as u  where p.Id = ph.PostId  	and u.Id = p.OwnerUserId 	and p.Id = v.PostId  AND ph.CreationDate>='2010-08-25 22:54:03'::timestamp  AND ph.CreationDate<='2014-09-06 23:45:29'::timestamp  AND p.PostTypeId=2  AND p.Score>=-3  AND u.Reputation>=1  AND u.Reputation<=135  AND u.DownVotes>=0  AND v.VoteTypeId=2;
