select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND ph.PostHistoryTypeId=2  AND p.PostTypeId=2  AND p.ViewCount<=2430  AND p.CommentCount>=0  AND u.DownVotes<=17  AND u.UpVotes>=0  AND u.UpVotes<=68  AND v.CreationDate>='2010-07-20 00:00:00'::timestamp  AND v.CreationDate<='2014-09-12 00:00:00'::timestamp;
