select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND ph.PostHistoryTypeId=2  AND pl.LinkTypeId=1  AND p.ViewCount>=0  AND p.CommentCount>=0  AND p.CommentCount<=11  AND p.CreationDate>='2009-02-02 14:24:58'::timestamp  AND p.CreationDate<='2014-08-12 19:37:54'::timestamp  AND u.UpVotes>=0  AND v.VoteTypeId=2;
