select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=1  AND p.PostTypeId=1  AND p.CreationDate>='2010-07-28 06:31:36'::timestamp  AND p.CreationDate<='2014-09-10 06:36:30'::timestamp  AND v.CreationDate>='2010-07-20 00:00:00'::timestamp;
