select  count(*) from postHistory as ph,          posts as p,  		users as u,  		badges as b  where u.Id = p.OwnerUserId 	and p.OwnerUserId = ph.UserId 	and ph.UserId = b.UserId  AND p.PostTypeId=2  AND p.Score>=-4  AND p.ViewCount>=0  AND p.AnswerCount<=5  AND p.CommentCount>=0  AND p.CommentCount<=11  AND p.FavoriteCount>=0  AND p.FavoriteCount<=11  AND u.Views<=28  AND u.UpVotes>=0;
