select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND c.Score=1  AND ph.PostHistoryTypeId=5  AND pl.CreationDate>='2012-01-26 18:30:44'::timestamp  AND pl.CreationDate<='2014-08-20 13:36:04'::timestamp  AND p.FavoriteCount<=2  AND v.CreationDate<='2014-09-12 00:00:00'::timestamp;
