select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND ph.PostHistoryTypeId=4  AND p.CommentCount>=0  AND p.CreationDate<='2014-08-21 11:23:36'::timestamp;
