select  count(*) from comments as c,  		posts as p,           votes as v,  		badges as b,          users as u  where u.Id =c.UserId 	and c.UserId = p.OwnerUserId  	and p.OwnerUserId = v.UserId 	and v.UserId = b.UserId  AND p.CommentCount<=11  AND p.FavoriteCount>=0  AND p.FavoriteCount<=5  AND u.DownVotes<=0  AND u.UpVotes>=0  AND u.CreationDate<='2014-09-10 17:18:36'::timestamp  AND v.BountyAmount>=0  AND v.BountyAmount<=50;
