select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.CreationDate<='2014-09-12 12:15:17'::timestamp  AND pl.LinkTypeId=1  AND p.Score<=25  AND p.AnswerCount<=3  AND p.FavoriteCount<=17;
