select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND b.Date>='2010-07-19 19:39:07'::timestamp  AND b.Date<='2014-09-14 00:09:35'::timestamp  AND p.CommentCount>=0  AND p.CommentCount<=11  AND u.Reputation<=3536  AND u.DownVotes>=0;
