select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND b.Date<='2014-09-12 09:02:04'::timestamp  AND ph.PostHistoryTypeId=4  AND pl.LinkTypeId=1  AND p.AnswerCount<=5  AND p.CommentCount<=9  AND p.FavoriteCount<=45  AND p.CreationDate<='2014-09-06 09:01:49'::timestamp;
