select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND b.Date<='2014-09-12 14:36:24'::timestamp  AND c.Score=0  AND ph.PostHistoryTypeId=2  AND p.CommentCount>=0  AND p.CommentCount<=17  AND p.CreationDate<='2014-08-31 23:35:23'::timestamp;
