select  count(*) from comments as c,          postLinks as pl,          posts as p,  		users as u,  		badges as b   where p.Id = pl.RelatedPostId 	and p.Id = c.PostId 	and u.Id = b.UserId 	and u.Id = p.OwnerUserId  AND c.Score=0  AND c.CreationDate>='2010-07-21 10:44:25'::timestamp  AND p.Score>=-1  AND p.AnswerCount=0  AND p.FavoriteCount>=0  AND p.FavoriteCount<=42  AND p.CreationDate>='2010-07-21 15:53:59'::timestamp  AND u.Views>=0  AND u.Views<=87  AND u.CreationDate<='2014-08-12 22:06:05'::timestamp;
