select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND pl.LinkTypeId=1  AND p.PostTypeId=1  AND p.Score>=-1  AND p.Score<=27  AND p.ViewCount>=0  AND p.ViewCount<=5973  AND p.CreationDate<='2014-09-09 03:20:03'::timestamp  AND u.Views=0  AND u.DownVotes>=0;
