select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND p.PostTypeId=2  AND p.AnswerCount>=0  AND p.CreationDate<='2014-09-04 14:38:16'::timestamp  AND u.DownVotes>=0  AND u.CreationDate>='2010-11-10 10:27:10'::timestamp  AND v.VoteTypeId=2;
