select  count(*) from comments as c,  		posts as p,           votes as v,  		badges as b,          users as u  where u.Id =c.UserId 	and c.UserId = p.OwnerUserId  	and p.OwnerUserId = v.UserId 	and v.UserId = b.UserId  AND c.Score=1  AND p.ViewCount>=0  AND p.ViewCount<=2638  AND p.AnswerCount<=4  AND p.FavoriteCount>=0  AND p.FavoriteCount<=5  AND u.Reputation>=1  AND u.Reputation<=4022  AND u.UpVotes<=20  AND v.VoteTypeId=2  AND v.CreationDate>='2010-07-20 00:00:00'::timestamp;
