select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND ph.CreationDate>='2010-08-27 09:36:24'::timestamp  AND p.CommentCount>=0  AND v.VoteTypeId=5;
