select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND p.PostTypeId=1  AND p.Score>=0  AND p.Score<=23  AND p.CreationDate>='2010-07-19 21:24:58'::timestamp  AND p.CreationDate<='2014-09-05 12:21:27'::timestamp  AND v.VoteTypeId=2;
