select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND c.CreationDate>='2009-04-12 08:13:05'::timestamp  AND pl.LinkTypeId=1  AND p.CommentCount>=0  AND p.FavoriteCount>=0  AND v.VoteTypeId=2;
