select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.CreationDate>='2010-08-10 14:59:40'::timestamp  AND pl.LinkTypeId=1  AND p.Score>=0  AND p.Score<=16  AND p.ViewCount>=0  AND p.AnswerCount<=3  AND p.CommentCount>=0  AND p.CommentCount<=8  AND p.CreationDate<='2014-09-02 19:10:17'::timestamp  AND v.VoteTypeId=2;
