select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.CreationDate<='2014-09-12 11:18:24'::timestamp  AND ph.PostHistoryTypeId=1  AND ph.CreationDate>='2010-09-09 23:09:59'::timestamp  AND p.ViewCount>=0  AND p.CommentCount>=0;
