select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND c.CreationDate<='2014-09-12 19:10:05'::timestamp  AND pl.CreationDate>='2011-02-22 15:52:50'::timestamp  AND pl.CreationDate<='2014-05-07 14:40:51'::timestamp  AND p.CommentCount>=0  AND p.CommentCount<=10  AND p.FavoriteCount<=9  AND p.CreationDate>='2010-07-19 23:45:44'::timestamp  AND p.CreationDate<='2014-08-27 18:17:17'::timestamp  AND v.VoteTypeId=2;
