select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND p.Score>=-1  AND p.ViewCount>=0  AND p.ViewCount<=3275  AND p.CommentCount<=17  AND p.CreationDate>='2010-08-10 06:55:21'::timestamp  AND u.DownVotes<=17;
