select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND c.CreationDate>='2010-08-05 15:45:53'::timestamp  AND c.CreationDate<='2014-08-26 13:27:45'::timestamp  AND pl.LinkTypeId=1  AND p.CommentCount>=0  AND p.CommentCount<=14  AND v.BountyAmount>=0  AND v.BountyAmount<=50  AND v.CreationDate>='2010-07-20 00:00:00'::timestamp;
