select  count(*) from comments as c,          postLinks as pl,          posts as p,  		users as u,  		badges as b   where p.Id = pl.RelatedPostId 	and p.Id = c.PostId 	and u.Id = b.UserId 	and u.Id = p.OwnerUserId  AND b.Date>='2010-08-30 03:27:52'::timestamp  AND c.CreationDate>='2010-07-28 15:37:02'::timestamp  AND p.ViewCount>=0  AND p.AnswerCount<=10  AND p.FavoriteCount>=0  AND u.Reputation=1  AND u.Views>=0  AND u.Views<=15;
