select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND ph.CreationDate>='2011-05-16 12:12:57'::timestamp  AND p.CommentCount>=0  AND p.FavoriteCount>=0  AND p.FavoriteCount<=6  AND p.CreationDate>='2010-07-20 19:08:50'::timestamp  AND p.CreationDate<='2014-08-21 23:01:04'::timestamp  AND u.Reputation>=1  AND u.UpVotes<=201  AND v.VoteTypeId=2;
