select  count(*) from comments as c,  		posts as p,  		postHistory as ph,          votes as v   where p.OwnerUserId = c.UserId 	and p.OwnerUserId = ph.UserId     and p.OwnerUserId = v.UserId  AND c.Score=1  AND ph.PostHistoryTypeId=3  AND p.Score<=22  AND p.ViewCount<=11083  AND p.CommentCount>=0  AND p.CommentCount<=15  AND p.FavoriteCount>=0  AND p.FavoriteCount<=10  AND v.VoteTypeId=2  AND v.BountyAmount>=0  AND v.BountyAmount<=50  AND v.CreationDate<='2014-09-06 00:00:00'::timestamp;
