select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND ph.PostHistoryTypeId=2  AND pl.LinkTypeId=1  AND p.CommentCount>=0  AND p.CommentCount<=11  AND u.CreationDate>='2010-07-27 02:56:06'::timestamp;
