select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND c.Score=0  AND pl.LinkTypeId=1  AND p.Score<=20  AND p.FavoriteCount<=7  AND p.CreationDate<='2014-09-10 16:53:34'::timestamp  AND v.VoteTypeId=2;
