select  count(*) from comments as c,  		posts as p,           postHistory as ph,  		badges as b,          users as u  where u.Id = c.UserId 	and u.Id = p.LastEditorUserId 	and u.Id = ph.UserId 	and u.Id = b.UserId  AND c.Score=0  AND p.PostTypeId=1  AND p.ViewCount>=0  AND p.AnswerCount>=0  AND p.FavoriteCount=1  AND p.CreationDate<='2014-09-10 16:16:01'::timestamp  AND u.Views>=0  AND u.DownVotes<=5  AND u.UpVotes<=3;
