select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND c.Score=0  AND p.PostTypeId=1  AND p.Score<=31  AND p.ViewCount<=1785  AND p.AnswerCount=0  AND p.FavoriteCount>=0  AND p.FavoriteCount<=21  AND v.CreationDate>='2010-07-21 00:00:00'::timestamp;
