select  count(*) from comments as c,  		posts as p,           postHistory as ph,  		badges as b,          users as u  where u.Id = c.UserId 	and u.Id = p.LastEditorUserId 	and u.Id = ph.UserId 	and u.Id = b.UserId  AND b.Date>='2010-08-03 20:00:23'::timestamp  AND c.Score=0  AND ph.PostHistoryTypeId=1  AND p.PostTypeId=2  AND p.Score>=0  AND p.Score<=28  AND p.AnswerCount>=0  AND p.FavoriteCount>=0  AND u.DownVotes<=0;
