select  count(*) from comments as c,  		posts as p,          postLinks as pl,          votes as v,          badges as b,          users as u  where p.Id = c.PostId 	and p.Id = pl.RelatedPostId     and p.Id = v.PostId  	and u.Id = p.LastEditorUserId     and u.Id = b.UserId  AND b.Date<='2014-09-11 17:10:33'::timestamp  AND p.Score<=156  AND p.CreationDate>='2010-07-22 11:31:16'::timestamp  AND u.UpVotes<=14  AND v.CreationDate>='2010-07-20 00:00:00'::timestamp;
