select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND b.Date<='2014-08-14 09:22:05'::timestamp  AND ph.PostHistoryTypeId=1  AND p.AnswerCount>=0  AND u.Reputation>=1  AND v.VoteTypeId=2  AND v.BountyAmount<=50;
