select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND p.Score<=16  AND p.AnswerCount>=0  AND p.AnswerCount<=3  AND p.CommentCount>=0  AND p.CommentCount<=22  AND u.UpVotes<=38;
