select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND c.CreationDate>='2010-08-09 08:05:01'::timestamp  AND c.CreationDate<='2014-09-11 14:53:43'::timestamp  AND ph.PostHistoryTypeId=2  AND pl.CreationDate>='2011-04-27 00:16:30'::timestamp  AND pl.CreationDate<='2014-09-03 22:40:38'::timestamp  AND p.PostTypeId=1  AND p.Score>=-2  AND p.AnswerCount=0  AND p.CommentCount>=0  AND p.FavoriteCount>=0  AND p.FavoriteCount<=8  AND v.VoteTypeId=2;
