select  count(*) from comments as c,  		posts as p,          postHistory as ph,          votes as v,          badges as b,          users as u  where u.Id = p.OwnerUserId     and u.Id = b.UserId     and p.Id = c.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND p.PostTypeId=1  AND p.Score>=-1  AND p.CommentCount>=0  AND p.CreationDate<='2014-08-18 15:56:46'::timestamp  AND u.Reputation<=811  AND u.DownVotes>=0  AND u.DownVotes<=7  AND u.UpVotes>=0  AND u.UpVotes<=260  AND v.VoteTypeId=2;
