select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND ph.PostHistoryTypeId=2  AND pl.CreationDate>='2011-07-20 17:17:56'::timestamp  AND pl.CreationDate<='2014-04-19 16:14:49'::timestamp  AND p.CommentCount<=20  AND p.FavoriteCount>=0;
