select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v,          users as u  where p.Id = pl.PostId 	and p.Id = c.PostId 	and p.Id = ph.PostId 	and p.Id = v.PostId 	and u.Id = p.LastEditorUserId  AND c.Score=0  AND ph.PostHistoryTypeId=3  AND pl.LinkTypeId=1  AND pl.CreationDate>='2010-11-12 01:06:02'::timestamp  AND p.PostTypeId=2  AND p.AnswerCount>=0  AND u.DownVotes<=4;
