select  count(*) from comments as c,  		posts as p,          postLinks as pl,          postHistory as ph,          votes as v,          badges as b  where p.Id = c.PostId     and p.Id = pl.RelatedPostId     and p.Id = ph.PostId     and p.Id = v.PostId 	and b.UserId = c.UserId  AND c.CreationDate>='2010-07-31 06:14:49'::timestamp  AND c.CreationDate<='2014-09-10 19:53:17'::timestamp  AND p.ViewCount<=3995  AND v.BountyAmount<=50  AND v.CreationDate>='2010-07-19 00:00:00'::timestamp;
