select  count(*) from comments as c,  		posts as p,  		postLinks as pl,          postHistory as ph,          votes as v  where p.Id = c.PostId 	and p.Id = pl.PostId     and p.Id = ph.PostId     and p.Id = v.PostId  AND pl.LinkTypeId=1  AND p.Score<=14  AND p.CommentCount>=0  AND p.FavoriteCount>=0  AND p.FavoriteCount<=21  AND v.CreationDate<='2014-09-09 00:00:00'::timestamp;
